    let dated = today.getDate() < 10 ? `0${today.getDate()}`: `${today.getDate()}`;
    return `${monthd}-${dated}-${today.getFullYear()}`;
  }
  dup() {
    this.duplicateImage = !this.duplicateImage ;
   // alert(`${this.duplicateImage}`)
